#include "Proxy.h"
#include <thrift/protocol/TBinaryProtocol.h>
#include <thrift/server/TSimpleServer.h>
#include <thrift/transport/TServerSocket.h>
#include <thrift/transport/TBufferTransports.h>
#include <time.h>
#include <curl/curl.h>
#include <iostream>
#include "cache.h"
#define EST (-4)
#define TIMEOUT 10

using namespace ::apache::thrift;
using namespace ::apache::thrift::protocol;
using namespace ::apache::thrift::transport;
using namespace ::apache::thrift::server;

using boost::shared_ptr;

using namespace  ::Proxy;

struct MemoryStruct {
    char* memory;
    size_t size;
};

static int cache_misses;

class ProxyHandler : virtual public ProxyIf {
 public:
  ProxyHandler() {
    // Your initialization goes here
      cache_misses = 0;
  }

  void ping() {
    // Your implementation goes here
      time_t rawtime;
      struct tm * ptm;
      time (&rawtime);
      ptm = gmtime ( &rawtime );
      puts ("Current time in Atlanta:");
      printf ("Atlanta, GA (U.S.) :  %2d:%02d\n", (ptm->tm_hour + EST) % 24, ptm->tm_min);
 
  }

  void getURL(std::string& _return, const std::string& url) {
      // Your implementation goes here
      std::cout << "Received request for url: " << url << std::endl;

      if (cache_get(url, _return) == 0)
      {
          std::cout<< "Cache hit. Forward contents from web cache." << std::endl;
          return; 
      }

      cache_misses++;
      CURL* curl;
      CURLcode res = CURLE_OK;

      struct MemoryStruct chunk;
      chunk.memory = (char*) malloc(1); /* will be grown as needed by realloc */
      chunk.size = 0;

      curl_global_init(CURL_GLOBAL_ALL);
      curl = curl_easy_init();
      if (curl)
      {
          /* specify url to get */
          curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
          /* follow redirection */
          curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);
          /* send all data to writer callback */
          curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &ProxyHandler::curlWriter);
          /* pass chunk struct to above callback function */
          curl_easy_setopt(curl, CURLOPT_WRITEDATA, (void*) &chunk);
          /* in case some server require user-agent field in HTTP header */
          curl_easy_setopt(curl, CURLOPT_USERAGENT, "libcurl-agent/1.0");

          res = curl_easy_perform(curl);
          
          if (res != CURLE_OK)
              fprintf(stderr, "curl_easy_perform() failed: %s\n", curl_easy_strerror(res));

          _return = std::string(chunk.memory);
          cache_set(url, _return);
          curl_easy_cleanup(curl); 
      }
  }

  int32_t getCacheMiss() {
    return cache_misses;
  }

 private:
    static size_t curlWriter(void* buf, size_t size, size_t nmemb, void* userp)
    {
        size_t realsize = size * nmemb;
        struct MemoryStruct *mem = (struct MemoryStruct *)userp;

        mem->memory = (char*) realloc(mem->memory, mem->size + realsize + 1);
        if(mem->memory == NULL) {
            /* out of memory! */ 
            printf("not enough memory (realloc returned NULL)\n");
            return 0;
        }

        memcpy(&(mem->memory[mem->size]), buf, realsize);
        mem->size += realsize;
        mem->memory[mem->size] = 0;

        return realsize;
    }

};

int main(int argc, char **argv) {
    if (argc != 3)
    {
        std::cout << "usage: " << argv[0] << " capacity min_entry_size";
        exit(1);
    }

    size_t capacity = atoi(argv[1]);
    size_t min_entry_size = atoi(argv[2]);

    cache_init(capacity, min_entry_size);

    int port = 9090;
    boost::shared_ptr<ProxyHandler> handler(new ProxyHandler());
    boost::shared_ptr<TProcessor> processor(new ProxyProcessor(handler));
    boost::shared_ptr<TServerTransport> serverTransport(new TServerSocket(port));
    boost::shared_ptr<TTransportFactory> transportFactory(new TBufferedTransportFactory());
    boost::shared_ptr<TProtocolFactory> protocolFactory(new TBinaryProtocolFactory());

    TSimpleServer server(processor, serverTransport, transportFactory, protocolFactory);
    server.serve();
    return 0;
}

